package com.cgday1;

public class basics {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=45;
		System.out.println("Add: " +(a+b));
		System.out.println("Sub: " +(a-b));
		System.out.println("Mul: " +(a*b));
		System.out.println("Div: " +(a/b));
		System.out.println("Mod: " +(a%b));
		
		

	}

}
